let VERIFY_TOKEN = env.get("FB_VERIFY_TOKEN");

// Parse the query params
let mode = msg.payload['hub.mode'];
let token = msg.payload['hub.verify_token'];
let challenge = msg.payload['hub.challenge'];

// Checks if a token and mode is in the query string of the request
if (mode && token) {

    // Checks the mode and token sent is correct
    if (mode === 'subscribe' && token === VERIFY_TOKEN) {
        // Responds with the challenge token from the request
        msg.payload = challenge;
        return [msg, null];

    } else {
        // Responds with '403 Forbidden' if verify tokens do not match
        return [null, msg];    
    }
}

return [null, null];

if (msg.payload.hasOwnProperty("object")) {
    if (msg.payload.object === 'page') {
        //set messenger type
        msg.messenger_type = "fb";
        msg.user = { 
            "user_id": msg.payload.entry[0].messaging[0].sender.id.toString()
        };

        //set message
        if (msg.payload.entry[0].messaging[0].hasOwnProperty("message")) {
            if (msg.payload.entry[0].messaging[0].message.hasOwnProperty("quick_reply")) {
                msg.text = msg.payload.entry[0].messaging[0].message.quick_reply.payload;
            } else {
                msg.text = msg.payload.entry[0].messaging[0].message.text;
            }
        } else if (msg.payload.entry[0].messaging[0].hasOwnProperty("postback")) {
            msg.text = msg.payload.entry[0].messaging[0].postback.payload;
        }


        // Returns a '200 OK' response to all requests
        msg.payload = 'EVENT_RECEIVED';
        return [null, null, msg, null];
    } else {
        // Returns a '404 Not Found' if event is not from a page subscription
        return [null, null, null, msg];
    }
} else if (msg.payload.hasOwnProperty("event")) {
    //set messenger type
    msg.messenger_type = "vb";

    //if setting webhook
    if (msg.payload.event == "webhook") {
        return [null, msg, null, null];
    } else if (msg.payload.event == "message") {
        msg.user = {
            "user_id": msg.payload.sender.id.toString()
        }

        //set message
        if (msg.payload.message.type == "text") {
            msg.text = msg.payload.message.text;
        }

        return [null, null, msg, null]
    } else if (msg.payload.event == "conversation_started") {
        msg.user = {
            "user_id": msg.payload.user.id.toString()
        }

        msg.template_id = "welcomeMessage";
        return [msg, null, null, null]
    }

} else if (msg.payload.hasOwnProperty("update_id")) {
    //set messenger type
    msg.messenger_type = "tg";
    msg.user = {
        "user_id": ((msg.payload.hasOwnProperty("message") ? msg.payload.message.chat.id : msg.payload.callback_query.from.id).toString()),
        "first_name": ((msg.payload.hasOwnProperty("message") && msg.payload.message.chat.first_name) ? msg.payload.message.chat.first_name : ""),
        "last_name": ((msg.payload.hasOwnProperty("message") && msg.payload.message.chat.last_name) ? msg.payload.message.chat.last_name : "")
    };

    //set message
    if (msg.payload.hasOwnProperty("message")) {
        msg.text = msg.payload.message.text;
    } else if (msg.payload.hasOwnProperty("callback_query")) {
        msg.text = msg.payload.callback_query.data;
    }

    return [null, null, msg, null];
}

return [null, null, null, msg];

